// src/services/api.js
import axios from "axios";

const BASE_URL = "http://localhost:5000/api/auth"; // auth base URL

// --- Auth APIs ---
export const loginAPI = async (credentials) => {
  const res = await axios.post(`${BASE_URL}/login`, credentials);
  return res.data; // should return { token: '...' }
};

export const signupAPI = async (userData) => {
  const res = await axios.post(`${BASE_URL}/signup`, userData);
  return res.data; // success message or user info
};

// --- Task APIs ---
const TASKS_URL = "http://localhost:5000/api/tasks";

export const getTasks = async (token) => {
  const res = await axios.get(TASKS_URL, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return res.data;
};

export const addTaskAPI = async (task, token) => {
  const res = await axios.post(TASKS_URL, task, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return res.data;
};

export const updateTaskAPI = async (task, token) => {
  const res = await axios.put(`${TASKS_URL}/${task.id}`, task, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return res.data;
};

export const deleteTaskAPI = async (id, token) => {
  const res = await axios.delete(`${TASKS_URL}/${id}`, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return res.data;
};
