const BASE_URL = "http://localhost:5000/api/tasks";

const handleResponse = async (res) => {
  if (!res.ok) {
    const text = await res.text(); // handle non-JSON errors like "Forbidden"
    throw new Error(text);
  }
  return res.json();
};

export const getTasks = async (token) => {
  const res = await fetch(BASE_URL, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return handleResponse(res);
};

export const addTaskAPI = async (task, token) => {
  const res = await fetch(BASE_URL, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${token}`,
    },
    body: JSON.stringify(task),
  });
  return handleResponse(res);
};

export const updateTaskAPI = async (task, token) => {
  const res = await fetch(`${BASE_URL}/${task.id}`, {
    method: "PUT",
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${token}`,
    },
    body: JSON.stringify(task),
  });
  return handleResponse(res);
};

export const deleteTaskAPI = async (id, token) => {
  const res = await fetch(`${BASE_URL}/${id}`, {
    method: "DELETE",
    headers: { Authorization: `Bearer ${token}` },
  });
  return handleResponse(res);
};
