import React, { useEffect, useState } from "react";
import { Container, Row, Col, Button, Form } from "react-bootstrap";
import AppNavbar from "../components/Navbar.js";
import TasksList from "../components/TasksList.js";
import TaskForm from "../components/TaskForm.js";
import { getTasks, createTask, updateTask, deleteTask } from "../services/api.js";

function DashboardPage() {
  const [tasks, setTasks] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);
  const [search, setSearch] = useState("");

  const fetchTasks = async () => {
    try {
      const res = await getTasks();
      setTasks(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleSaveTask = async (task) => {
    try {
      if (currentTask) {
        await updateTask(currentTask.id, task);
      } else {
        await createTask(task);
      }
      setShowForm(false);
      setCurrentTask(null);
      fetchTasks();
    } catch (err) {
      console.error(err);
    }
  };

  const handleEditTask = (task) => {
    setCurrentTask(task);
    setShowForm(true);
  };

  const handleDeleteTask = async (id) => {
    try {
      await deleteTask(id);
      fetchTasks();
    } catch (err) {
      console.error(err);
    }
  };

  const filteredTasks = tasks.filter((t) => t.title.toLowerCase().includes(search.toLowerCase()));

  return (
    <>
      <AppNavbar />
      <Container className="mt-4">
        <Row className="mb-3">
          <Col md={6}>
            <Form.Control
              type="text"
              placeholder="Search tasks..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
          </Col>
          <Col md={6} className="text-end">
            <Button onClick={() => setShowForm(true)}>Add Task</Button>
          </Col>
        </Row>
        <TasksList tasks={filteredTasks} handleEdit={handleEditTask} handleDelete={handleDeleteTask} />
      </Container>
      <TaskForm show={showForm} handleClose={() => setShowForm(false)} handleSave={handleSaveTask} task={currentTask} />
    </>
  );
}

export default DashboardPage;
