import axios from "axios";

// Use correct port — must match your backend
const API_BASE_URL = "http://localhost:5000";

export const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    "Content-Type": "application/json",
  },
});

export const loginAPI = (data) => api.post("/api/auth/login", data);
export const signupAPI = (data) => api.post("/api/auth/signup", data);
export const getTasks = (token) =>
  api.get("/api/tasks", { headers: { Authorization: `Bearer ${token}` } });
export const addTaskAPI = (task, token) =>
  api.post("/api/tasks", task, { headers: { Authorization: `Bearer ${token}` } });
export const updateTaskAPI = (task, token) =>
  api.put(`/api/tasks/${task.id}`, task, { headers: { Authorization: `Bearer ${token}` } });
export const deleteTaskAPI = (id, token) =>
  api.delete(`/api/tasks/${id}`, { headers: { Authorization: `Bearer ${token}` } });
