// src/services/api.js
import axios from "axios";

const BASE_URL = "http://localhost:5000"; // your backend URL

// --- Auth APIs ---
export const loginAPI = async (credentials) => {
  const res = await axios.post(`${BASE_URL}/api/login`, credentials);
  return res.data; // should return { token: '...' }
};

export const signupAPI = async (userData) => {   // <-- add this
  const res = await axios.post(`${BASE_URL}/api/signup`, userData);
  return res.data; // success message or user info
};

// --- Task APIs ---
export const getTasks = async (token) => {
  const res = await axios.get(`${BASE_URL}/api/tasks`, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return res.data;
};

export const addTaskAPI = async (task, token) => {
  const res = await axios.post(`${BASE_URL}/api/tasks`, task, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return res.data;
};

export const updateTaskAPI = async (task, token) => {
  const res = await axios.put(`${BASE_URL}/api/tasks/${task.id}`, task, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return res.data;
};

export const deleteTaskAPI = async (id, token) => {
  const res = await axios.delete(`${BASE_URL}/api/tasks/${id}`, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return res.data;
};
