import React, { useState, useEffect } from "react";
import { Container, Row, Col, Card, Table, Form, Button, InputGroup } from "react-bootstrap";

// Navbar
function AppNavbar({ onLogout }) {
  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-primary mb-4">
      <Container>
        <span className="navbar-brand">Task Dashboard</span>
        <button className="btn btn-outline-light ms-auto" onClick={onLogout}>
          Logout
        </button>
      </Container>
    </nav>
  );
}

// TaskForm Component
function TaskForm({ onAddTask, editTask, onUpdateTask, cancelEdit }) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [status, setStatus] = useState("Pending");

  useEffect(() => {
    if (editTask) {
      setTitle(editTask.title);
      setDescription(editTask.description);
      setStatus(editTask.status);
    }
  }, [editTask]);

  const token = localStorage.getItem("token");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title.trim()) return alert("Title is required!");

    const taskData = { title, description, status };

    try {
      if (editTask) {
        const res = await fetch(`http://localhost:5000/api/tasks/${editTask.id}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(taskData),
        });
        const updatedTask = await res.json();
        onUpdateTask(updatedTask);
      } else {
        const res = await fetch("http://localhost:5000/api/tasks", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(taskData),
        });
        const newTask = await res.json();
        onAddTask(newTask);
      }

      setTitle("");
      setDescription("");
      setStatus("Pending");
    } catch (error) {
      console.error("Error saving task:", error);
    }
  };

  return (
    <Card className="mb-4">
      <Card.Body>
        <Card.Title>{editTask ? "Edit Task" : "Add Task"}</Card.Title>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-2">
            <Form.Label>Title</Form.Label>
            <Form.Control
              type="text"
              placeholder="Task Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </Form.Group>
          <Form.Group className="mb-2">
            <Form.Label>Description</Form.Label>
            <Form.Control
              as="textarea"
              placeholder="Task Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </Form.Group>
          <Form.Group className="mb-2">
            <Form.Label>Status</Form.Label>
            <Form.Select value={status} onChange={(e) => setStatus(e.target.value)}>
              <option>Pending</option>
              <option>In Progress</option>
              <option>Completed</option>
            </Form.Select>
          </Form.Group>
          <Button type="submit" className="me-2">
            {editTask ? "Update Task" : "Add Task"}
          </Button>
          {editTask && <Button variant="secondary" onClick={cancelEdit}>Cancel</Button>}
        </Form>
      </Card.Body>
    </Card>
  );
}

// TasksList Component
function TasksList({ tasks, onEdit, onDelete }) {
  const token = localStorage.getItem("token");

  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5000/api/tasks/${id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      onDelete(id);
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  return (
    <Card>
      <Card.Body>
        <Card.Title>Tasks</Card.Title>
        {tasks.length === 0 ? (
          <p>No tasks available</p>
        ) : (
          <Table striped bordered hover responsive>
            <thead>
              <tr>
                <th>Title</th>
                <th>Description</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {tasks.map((task) => (
                <tr key={task.id}>
                  <td>{task.title}</td>
                  <td>{task.description}</td>
                  <td>{task.status}</td>
                  <td>
                    <Button size="sm" variant="info" className="me-2" onClick={() => onEdit(task)}>
                      Edit
                    </Button>
                    <Button size="sm" variant="danger" onClick={() => handleDelete(task.id)}>
                      Delete
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        )}
      </Card.Body>
    </Card>
  );
}

// Dashboard Page
function DashboardPage({ onLogout }) {
  const [tasks, setTasks] = useState([]);
  const [editTask, setEditTask] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const token = localStorage.getItem("token");

  const fetchTasks = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/tasks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setTasks(data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const addTask = (task) => setTasks([...tasks, task]);
  const updateTask = (updatedTask) => {
    setTasks(tasks.map((t) => (t.id === updatedTask.id ? updatedTask : t)));
    setEditTask(null);
  };
  const deleteTask = (id) => setTasks(tasks.filter((t) => t.id !== id));
  const editExistingTask = (task) => setEditTask(task);
  const cancelEdit = () => setEditTask(null);

  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <>
      <AppNavbar onLogout={onLogout} />
      <Container>
        <Row>
          <Col md={4}>
            <TaskForm
              onAddTask={addTask}
              editTask={editTask}
              onUpdateTask={updateTask}
              cancelEdit={cancelEdit}
            />
          </Col>
          <Col md={8}>
            <InputGroup className="mb-3">
              <Form.Control
                placeholder="Search tasks..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </InputGroup>
            <TasksList tasks={filteredTasks} onEdit={editExistingTask} onDelete={deleteTask} />
          </Col>
        </Row>
      </Container>
    </>
  );
}

export default DashboardPage;
